var series = ["강다니엘","방탄소년단","청하","아이유","박서준"];

var dataset = [ 
    {'1': 11, '2':11, '3':22, '4':27, '5':32, '6':30, '7':22}, //i=1
    {'1': 32, '2':21, '3':29, '4':27, '5':33, '6':22, '7':15}, //i=2
    {'1': 25, '2':14, '3':24, '4':22, '5':27, '6':20, '7':19},
    {'1': 22, '2':19, '3':19, '4':19, '5':22, '6':16, '7':20},
    {'1': 19, '2':17, '3':22, '4':39, '5':35, '6':34, '7':39}];//i=5

var keys = d3.keys(dataset[0]);
var data = [];

dataset.forEach(function(d, i) {
data[i] = keys.map(function(key) { return {series : series[i] ,x: key, y: d[key]}; })
});
//data[1] = [ { series : 1 , x : 1, y : 11} , { series : 1, x : 2 , y: 11} , { .....} };
//data[2] = [ { series : 2 , x : 2, y : 32} , { sereies : ... } };
var margin = {left: 20, top: 40, right: 10, bottom: 20};
var svg = d3.select("svg");
var width  = parseInt(svg.style("width"), 10) - margin.left - margin.right;
var height = parseInt(svg.style("height"), 10)- margin.top  - margin.bottom;
var svgG = svg.append("g")
    .attr("transform", "translate(" + margin.left + "," + margin.top + ")");
var xScale = d3.scalePoint()//scaleBand() scaleOrdinal
    .domain(keys)
    .rangeRound([0, width]);
var yScale = d3.scaleLinear()
    .domain([0, d3.max(dataset, function(d) { return d3.max(keys, function(key) { return d[key];});})])
    .nice()
    .range([height, 0]);
var colors = d3.scaleOrdinal(d3.schemeCategory10);

svgG.append("g")
    .attr("class", "grid")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(xScale)
        .tickSize(-height)
    );

svgG.append("g")
    .attr("class", "grid")
    .call(d3.axisLeft(yScale)
        .ticks(5)
        .tickSize(-width)
       );

var line = d3.line()
    //.curve(d3.curveBasis)
    .x(function(d) { return xScale(d.x); })
    .y(function(d) { return yScale(d.y); });
var lineG = svgG.append("g")
    .selectAll("g")
    .data(data)
    .enter().append("g");

lineG.append("path")
    .attr("class", "lineChart")
    .style("stroke", function(d, i) { return colors( series[i]); })
    .attr("d", function(d, i) {return line(d); });

lineG.selectAll("dot")
    .data(function(d) {return d })
    .enter().append("circle")
        .attr("r", 3)
        .attr("cx", function(d) { return xScale(d.x) })
        .attr("cy", function(d) { return yScale(d.y);})
        .on("mouseover", function() { tooltip.style("display", null); })
        .on("mouseout",  function() { tooltip.style("display", "none"); })
        .on("mousemove", function(d,i) {
            tooltip.style("left", (d3.event.pageX+10)+"px");
            tooltip.style("top",  (d3.event.pageY-10)+"px");
            tooltip.html("line: " + d.series +"<br/>" + "days. " + d.x + "<br/>" + "data value : " + d.y);
        });

var tooltip = d3.select("body")
    .append("div")
    .attr("class", "toolTip")
    .style("display", "none");

var legend = svgG.append("g")
    .attr("text-anchor", "start")
    .selectAll("g")
    .data(series)
    .enter().append("g")
    .attr("transform", function(d, i) { return "translate("+i*120+",0)"; });

legend.append("rect")
      .attr("x", 2)
      .attr("y", -24)
      .attr("width", 19)
      .attr("height", 19)
      .attr("fill", colors);

  legend.append("text")
      .attr("x", 27)
      .attr("y",-14)
      .attr("dy", "0.32em")
      .text(function(d) { return d; });